import { useEffect, useState } from "react";
import MemeList from './MemeList';

function FavList(){

    const [favArr, setFavArr] = useState([]);
    const sendReq = async () => {
        const serverURL = `http://localhost:3002/favMeme`;
        const response = await fetch(serverURL);
        const data = await response.json();
        console.log(data)
        setFavArr(data);
    }

    useEffect(()=>{
        sendReq();
    }, [])

    return(
        <>
        <h1>FavList</h1>
        <Row xs={1} md={4} className="g-4">
                {favArr.map((item) => {
                    return <Col>
                        <Card style={{ width: '18rem' }}>
                            <Card.Img variant="top" src={item.image_path} />
                            <Card.Body>
                                <Card.Title>{item.meme_name}</Card.Title>
                                <Card.Text>
                                    <p>{item.top_text}</p>
                                    <p>{item.tags}</p>
                                </Card.Text>
                                <Button variant="primary" onClick={() => {handleShow(item)}}>more options</Button>
                            </Card.Body>
                        </Card>
                    </Col>
                })}
            </Row>

            <MemeModal showFlag={showFlag} handleclose={handleclose} memeData={clickedMeme}/>
        </>
    );
}

export default FavList;
